import React, { Component } from 'react';
import CardList from '../components/CardList';
import SearchBox from '../components/SearchBox';
import Scroll from '../components/Scroll';

class App extends Component {
	constructor() {
			super()
			this.state = {
				robot: [],
				searchfield: ''
			}
		}

	componentDidMount() {
		fetch('https://jsonplaceholder.typicode.com/users')
		 .then(response => {return response.json();})
		 .then(users => {this.setState({robot: users});});
		}

		onSearchChange = (event) => {this.setState({ searchfield: event.target.value})}

				/* MAKE CODE CLEANER
				You can distructure 'this.state', BY creating a variable:
					const { robot, searchfield} = this.state;  
				and remove this.state from the code: this.state.robot
				or this.state.searchfield . */


		render() {
				const {robot, searchfield} = this.state;
				const filterRobots = robot.filter(rob => {
					return rob.name.toLowerCase().includes(searchfield.toLowerCase());
				})
			return 	!robot.length ? // this means: if robot is not zero, write Loading...
				<h1>Loading...</h1> : // this means: else, render the code below
					(
						<div className='tc'>
							<h1 className= 'f1'>Robofriends</h1>
							<SearchBox searchChange={this.onSearchChange}/>
							<Scroll>
						    	<CardList robots={filterRobots}/>
						    </Scroll>
					   </div>
			 		);
			}
	}

export default App;
