import React, { Component } from 'react';
import CardList from '../components/CardList';
import SearchBox from '../components/SearchBox';
import Scroll from '../components/Scroll';

class App extends Component {
	constructor() {
			super()
			this.state = {
				robot: [],
				searchfield: ''
			}
		}

	componentDidMount() {
		fetch('https://jsonplaceholder.typicode.com/users')
		 .then(response => {return response.json();})
		 .then(users => {this.setState({robot: users});});
		}

		onSearchChange = (event) => {this.setState({ searchfield: event.target.value})}

				/* MAKE CODE CLEANER
				You can distructure 'this.state', BY creating a variable:
					const { robot, searchfield} = this.state;  ...right below render()
				and remove this.state from the code below. */


		render() 
				{const filterRobots = this.state.robot.filter(rob => {
					return rob.name.toLowerCase().includes(this.state.searchfield.toLowerCase());
				})
		return 
				 /*!robot.length ?  if robot array is too big write Loading, else render the code
									! is a NOT symbol (in this case means different than 0)
									This line will go through the array and as it reads thru,
									the h1 tag Loading is being displayed. As soon it finishes to read the array
									it will render the code. 
			<h1>Loading...</h1> : // this colon means else, don't miss it  */
		
			(
				<div className='tc'>
					<h1 className= 'f1'>Robofriends</h1>
					<SearchBox searchChange={this.onSearchChange}/>
					<Scroll>
				    	<CardList robots={filterRobots}/>
				    </Scroll>
			   </div>
		 	);
	}
}

export default App;
