import React from 'react';

const Card =(props) => {
	const {name, email, id} = props;
	return(
			<div className='tc bg-dark-green dib br4 pa1 ma2 grow shadow-5'> 
				<img src={`https://robohash.org/${id}?200x200`} alt='robots'/>    
				<h2>{name}</h2>
				<p>{email}</p>
			</div>			
	);
}

export default Card;
